import{_ as r,a as d}from"./chunks/right-click-send.BINmcvzf.js";import{_ as o,c as s,o as e,ag as n}from"./chunks/framework.Bw-5EFTY.js";const T=JSON.parse('{"title":"Composer 界面","description":"","frontmatter":{},"headers":[],"relativePath":"docs/gui/composer.md","filePath":"docs/gui/composer.md"}'),a={name:"docs/gui/composer.md"};function g(i,t,l,p,m,c){return e(),s("div",null,t[0]||(t[0]=[n('<h1 id="composer-界面" tabindex="-1">Composer 界面 <a class="header-anchor" href="#composer-界面" aria-label="Permalink to &quot;Composer 界面&quot;">​</a></h1><p>Composer 是 Whistle 提供的 HTTP 请求构造工具，可用于快速创建、修改和发送自定义请求，主要用途是对抓包数据快速编辑及模拟请求，满足联调过程中快速定位接口问题的需求。</p><img src="'+r+'" alt="Composer 界面" width="420"><p>右键点击发送按钮：</p><img src="'+d+'" alt="右键点击发送按钮" width="260"><table tabindex="0"><thead><tr><th>组件</th><th>功能</th></tr></thead><tbody><tr><td><strong>历史记录按钮</strong></td><td>显示或隐藏历史记录</td></tr><tr><td><strong>选择方法</strong></td><td>选择请求方法，支持 GET/POST/PUT 等多种种方法</td></tr><tr><td><strong>URL 输入框</strong></td><td>编辑或输入请求 URL径</td></tr><tr><td><strong>Params</strong></td><td>添加、修改、删除请求参数</td></tr><tr><td><strong>发送按钮</strong></td><td>执行当前请求</td></tr><tr><td><strong>Send Body Via File</strong></td><td>从本地文件加载请求内容，用于发送大块请求内容</td></tr><tr><td><strong>Replay Times</strong></td><td>设置重放次数，最多 100 次</td></tr><tr><td><strong>Show History</strong></td><td>显示历史记录，跟左侧的图标按钮功能一样</td></tr><tr><td><strong>Rules</strong></td><td>是否启用下方输入框里面的规则</td></tr><tr><td><strong>Whistle</strong></td><td>否启用 Whistle 里面配置的规则</td></tr><tr><td><strong>Pretty</strong></td><td>格式化显示内容</td></tr><tr><td><strong>Body</strong></td><td>是否包含请求体（GET/HEAD/OPTIONS 等方法会自动忽略请求体）</td></tr><tr><td><strong>HTTP/2</strong></td><td>是否使用 HTTP/2 协议</td></tr><tr><td><strong>Import</strong></td><td>导入请求数据</td></tr><tr><td><strong>Export</strong></td><td>导出请求数据</td></tr><tr><td><strong>AsCURL</strong></td><td>生成可执行的 cURL 命令</td></tr><tr><td><strong>规则输入框</strong></td><td>自定义规则，只对当前构造的请求生效</td></tr><tr><td><strong>请求头</strong></td><td>自定义请求头</td></tr><tr><td><strong>请求内容</strong></td><td>自定义请求内容</td></tr></tbody></table><h2 id="保存请求-响应数据" tabindex="-1">保存请求/响应数据 <a class="header-anchor" href="#保存请求-响应数据" aria-label="Permalink to &quot;保存请求/响应数据&quot;">​</a></h2><ol><li>点击发送时自动保存到 Composer 历史记录里面（最多保存 64 条记录）</li><li>在抓包列表中右键抓包数据并点击 Save 保存</li></ol>',8)]))}const P=o(a,[["render",g]]);export{T as __pageData,P as default};
