import{_ as e,a as i,b as o,c as a}from"./chunks/proxy-settings.D0kHdlLN.js";import{_ as r,c as l,o as s,ae as n}from"./chunks/framework.Dgg8-8ov.js";const y=JSON.parse('{"title":"Mobile Packet Capture","description":"","frontmatter":{},"headers":[],"relativePath":"en/docs/mobile.md","filePath":"en/docs/mobile.md"}'),c={name:"en/docs/mobile.md"};function u(h,t,d,p,f,g){return s(),l("div",null,t[0]||(t[0]=[n('<h1 id="mobile-packet-capture" tabindex="-1">Mobile Packet Capture <a class="header-anchor" href="#mobile-packet-capture" aria-label="Permalink to &quot;Mobile Packet Capture&quot;">​</a></h1><p>Mobile packet capture debugging requires the following configuration:</p><ol><li>Install the root certificate (required for HTTPS packet capture)</li><li>Set up the system proxy</li></ol><h2 id="install-the-root-certificate-required-for-https-packet-capture" tabindex="-1">Install the root certificate (required for HTTPS packet capture) <a class="header-anchor" href="#install-the-root-certificate-required-for-https-packet-capture" aria-label="Permalink to &quot;Install the root certificate (required for HTTPS packet capture)&quot;">​</a></h2><h4 id="downloading-the-root-certificate" tabindex="-1">Downloading the root certificate: <a class="header-anchor" href="#downloading-the-root-certificate" aria-label="Permalink to &quot;Downloading the root certificate:&quot;">​</a></h4><ol><li><p>Click the HTTPS button at the top of the Whistle interface. The HTTPS Settings dialog box will pop up.</p><img width="320" alt="HTTPS Settings" src="'+e+'"></li><li><p>Use your phone&#39;s camera to scan the QR code in the dialog box → Click the link that pops up. (If the download fails, try another QR code until the scan succeeds.)</p><img width="320" alt="Scan qrcode" src="'+i+'"><blockquote><p>If the native Android browser fails to download, try using Chrome to download the certificate: http://[Computer IP]:[Whistle port]/cgi-bin/rootca, or download the certificate from a PC and transfer it to your phone.</p><p>If all QR codes fail to download:</p><ul><li>Check that your device and Whistle host are on the same local area network.</li><li>Verify that your firewall isn&#39;t blocking the proxy port.</li></ul></blockquote></li><li><p>After the download is successful, record the IP and port number of the QR code address for use in setting up the system proxy. Then, install and trust the root certificate as follows.</p></li></ol><h4 id="installing-the-root-trust-certificate" tabindex="-1">Installing the Root Trust Certificate: <a class="header-anchor" href="#installing-the-root-trust-certificate" aria-label="Permalink to &quot;Installing the Root Trust Certificate:&quot;">​</a></h4><p><strong>iOS</strong></p><ol><li><p>Install the profile</p><ul><li>Go to: Settings → General → VPN &amp; Device Management</li><li>Find the &quot;Downloaded Profile&quot; and install it.</li></ul></li><li><p>Enable Full Trust (this step is essential).</p><ul><li>Go to: Settings → General → About → Certificate Trust Settings</li><li>Enable Full Trust for the Whistle root certificate.</li></ul><img width="320" alt="Certificate Trust Settings" src="'+o+'"></li></ol><p><strong>Android</strong></p><ol><li>Go to: Settings → Security → Encryption &amp; Credentials → Install Certificates → CA Certificates</li><li>Select the downloaded certificate file.</li><li>Enter your lock screen passcode to confirm.</li><li>Name the certificate (e.g., &quot;Whistle&quot;).</li></ol><blockquote><p><strong>Version Differences:</strong></p><p>Android 12+: Requires &quot;More Security Settings&quot;</p><p>Huawei EMUI: Requires &quot;Clean Mode&quot; to be disabled first</p><p>Other brands: The path may vary slightly</p></blockquote><h2 id="set-up-the-system-proxy" tabindex="-1">Set up the system proxy <a class="header-anchor" href="#set-up-the-system-proxy" aria-label="Permalink to &quot;Set up the system proxy&quot;">​</a></h2><ol><li>Access Wi-Fi settings <ul><li>Go to: Settings → Wi-Fi</li><li>Tap the icon next to the currently connected network (Android may require long-pressing the Wi-Fi name).</li></ul></li><li>Configure a manual proxy <ul><li>Select &quot;Manual&quot; for the proxy type.</li><li>Server: Enter the IP address from which the certificate QR code was successfully downloaded.</li><li>Port: Enter the port from which the certificate QR code was successfully downloaded (Whistle defaults to port 8899).</li><li>Save settings</li></ul></li></ol><img width="320" alt="Set Proxy" src="'+a+'"><h2 id="successful-root-certificate-installation-and-proxy-setup-indicator" tabindex="-1">Successful root certificate installation and proxy setup indicator <a class="header-anchor" href="#successful-root-certificate-installation-and-proxy-setup-indicator" aria-label="Permalink to &quot;Successful root certificate installation and proxy setup indicator&quot;">​</a></h2><ol><li>Accessing the web works fine on my phone.</li><li>Whistle can capture HTTPS requests.</li><li>No security warnings appear.</li></ol><h2 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-label="Permalink to &quot;FAQ&quot;">​</a></h2><ol><li>Certificate is untrusted. <ul><li>Check that &quot;Full Trust&quot; is set up (iOS).</li><li>Confirm that the certificate is installed in the correct location (Android).</li></ul></li><li>Proxy is not working. <ul><li>Check the &quot;Online&quot; dialog box in the upper right corner of the Whistle interface to see if the IP and port have changed.</li><li>If you&#39;re not sure which IP address is available, try each one.</li></ul></li><li>Packet capture fails for a specific app. <ul><li>Check that the app uses a custom certificate.</li><li>Try adding network configuration to the AndroidManifest.xml file. For details, see: <a href="https://developer.android.com/training/articles/security-config#base-config" target="_blank" rel="noreferrer">https://developer.android.com/training/articles/security-config#base-config</a></li></ul></li></ol>',19)]))}const q=r(c,[["render",u]]);export{y as __pageData,q as default};
