import{_ as s,c as a,o as t,ag as n}from"./chunks/framework.Bw-5EFTY.js";const u=JSON.parse('{"title":"cssAppend","description":"","frontmatter":{},"headers":[],"relativePath":"en/docs/rules/cssAppend.md","filePath":"en/docs/rules/cssAppend.md"}'),o={name:"en/docs/rules/cssAppend.md"};function i(l,e,p,r,d,c){return t(),a("div",null,e[0]||(e[0]=[n(`<h1 id="cssappend" tabindex="-1">cssAppend <a class="header-anchor" href="#cssappend" aria-label="Permalink to &quot;cssAppend&quot;">​</a></h1><p>The <code>cssAppend</code> protocol is used to insert specified content at the end of an existing CSS-type response body.</p><blockquote><p><strong>Effective Condition</strong>: Only takes effect on responses with a <code>content-type</code> header containing <code>html</code> or <code>css</code>, and a status code that includes a response body (e.g., <code>200</code>/<code>500</code>, etc.). ⚠️ Note: Requests without a response body, such as <code>204</code>, <code>304</code>, are not affected. HTML pages will automatically be wrapped in <code>&lt;style&gt;...&lt;/style&gt;</code>.</p></blockquote><p>With the <code>cssAppend</code> protocol, you can:</p><ul><li>Append new style rules to the end of existing CSS files.</li><li>Dynamically add CSS override rules.</li><li>Inject custom styles for specific pages.</li><li>Extend styling functionality without modifying the source files.</li></ul><h2 id="rule-syntax" tabindex="-1">Rule Syntax <a class="header-anchor" href="#rule-syntax" aria-label="Permalink to &quot;Rule Syntax&quot;">​</a></h2><p><code>cssAppend</code> supports multiple ways to specify the content to be appended:</p><h3 id="_1-inline-value-direct-specification" tabindex="-1">1. Inline Value (Direct Specification) <a class="header-anchor" href="#_1-inline-value-direct-specification" aria-label="Permalink to &quot;1. Inline Value (Direct Specification)&quot;">​</a></h3><p>Write the CSS content to be appended directly in the rule.</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># No spaces or line breaks are allowed inside the parentheses of \`value\`.</span></span>
<span class="line"><span>pattern cssAppend://(value) [lineProps...] [filters...]</span></span></code></pre></div><p><strong>Example:</strong></p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>www.example.com/index.html cssAppend://(.custom-btn{background:red;})</span></span></code></pre></div><h3 id="_2-embedded-value-using-code-block" tabindex="-1">2. Embedded Value (Using Code Block) <a class="header-anchor" href="#_2-embedded-value-using-code-block" aria-label="Permalink to &quot;2. Embedded Value (Using Code Block)&quot;">​</a></h3><p>Use this method when the CSS content to be appended contains spaces, line breaks, or needs to be reused.</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>pattern cssAppend://{custom-key} [lineProps...] [filters...]</span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`\`\` custom-key</span></span>
<span class="line"><span>.dark-mode {</span></span>
<span class="line"><span>  background: #333;</span></span>
<span class="line"><span>  color: #fff;</span></span>
<span class="line"><span>}</span></span>
<span class="line"><span>\`\`\`</span></span></code></pre></div><h3 id="_3-referencing-a-value-from-the-values-panel" tabindex="-1">3. Referencing a Value from the Values Panel <a class="header-anchor" href="#_3-referencing-a-value-from-the-values-panel" aria-label="Permalink to &quot;3. Referencing a Value from the Values Panel&quot;">​</a></h3><p>Reference CSS content pre-defined in the <code>Values</code> panel (central configuration area).</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>pattern cssAppend://{key-of-values} [lineProps...] [filters...]</span></span></code></pre></div><p><strong>Prerequisite:</strong> A key named <code>key-of-values</code> with CSS content as its value must exist in <code>Values</code>.</p><h3 id="_4-loading-from-a-temporary-file" tabindex="-1">4. Loading from a Temporary File <a class="header-anchor" href="#_4-loading-from-a-temporary-file" aria-label="Permalink to &quot;4. Loading from a Temporary File&quot;">​</a></h3><p>Use Whistle&#39;s temporary file feature when content needs frequent editing.</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>pattern cssAppend://temp.css</span></span></code></pre></div><p><strong>Steps:</strong></p><ol><li>In the Rules editor, hold down <code>Command</code> (Mac) / <code>Ctrl</code> (Windows)</li><li>Click with the mouse on <code>cssAppend://temp.css</code></li><li>Enter the response content in the pop-up editing dialog</li><li>Click <code>Save</code> to save</li></ol><p>After saving, the rule will automatically change to a format similar to this:</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://example.com/test.css cssAppend://temp/11adb9c9e1142df67b30d7646ec59bcd34c855d9011d1a2405c7fc2dfc94568d.css</span></span></code></pre></div><p>To edit again, click the temporary file link in the same way.</p><h3 id="_5-loading-from-a-file-or-remote-url" tabindex="-1">5. Loading from a File or Remote URL <a class="header-anchor" href="#_5-loading-from-a-file-or-remote-url" aria-label="Permalink to &quot;5. Loading from a File or Remote URL&quot;">​</a></h3><p>Load the CSS content to be appended from a local file or remote URL.</p><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># Load from a local file</span></span>
<span class="line"><span>pattern cssAppend:///User/xxx/custom.css</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Load from a remote URL (supports http and https)</span></span>
<span class="line"><span>pattern cssAppend://https://cdn.example.com/styles/override.css</span></span></code></pre></div><h2 id="parameter-details" tabindex="-1">Parameter Details <a class="header-anchor" href="#parameter-details" aria-label="Permalink to &quot;Parameter Details&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Parameter</th><th style="text-align:left;">Required</th><th style="text-align:left;">Description &amp; Examples</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>pattern</strong></td><td style="text-align:left;">Yes</td><td style="text-align:left;">Expression used to match the request URL.<br>• Supports domains, paths, wildcards, regular expressions.<br>• See <a href="./pattern.html">Matching Pattern Documentation</a> for details.</td></tr><tr><td style="text-align:left;"><strong>value</strong></td><td style="text-align:left;">Yes</td><td style="text-align:left;">The CSS content to be appended, supporting multiple formats:<br>• Plain CSS code text<br>• Supports references from local files, remote URLs, inline, embedded, and Values</td></tr><tr><td style="text-align:left;"><strong>lineProps</strong></td><td style="text-align:left;">No</td><td style="text-align:left;">Sets additional properties for the rule.<br>• Example: <code>lineProps://important</code> can increase this rule&#39;s priority.<br>• See <a href="./lineProps.html">lineProps Documentation</a> for details.</td></tr><tr><td style="text-align:left;"><strong>filters</strong></td><td style="text-align:left;">No</td><td style="text-align:left;">Optional filter conditions for precise control over when the rule takes effect.<br>• Can match request URL, method, headers, body content.<br>• Can match response status code, headers.<br>• See <a href="./filters.html">Filters Documentation</a> for details.</td></tr></tbody></table><p><strong>Special Notes:</strong></p><ul><li>Only takes effect on requests where the <code>content-type</code> response header contains <code>css</code>.</li><li>Does not take effect on status codes without a response body, such as <code>204</code>, <code>304</code>.</li><li>Appended content is directly concatenated to the end of the original CSS file.</li></ul><h2 id="configuration-examples" tabindex="-1">Configuration Examples <a class="header-anchor" href="#configuration-examples" aria-label="Permalink to &quot;Configuration Examples&quot;">​</a></h2><h4 id="scenario-1-adding-responsive-breakpoints" tabindex="-1">Scenario 1: Adding Responsive Breakpoints <a class="header-anchor" href="#scenario-1-adding-responsive-breakpoints" aria-label="Permalink to &quot;Scenario 1: Adding Responsive Breakpoints&quot;">​</a></h4><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>www.example.com/responsive.css cssAppend://{test.css}</span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`\`\` test.css</span></span>
<span class="line"><span>@media (max-width: 768px) {</span></span>
<span class="line"><span>  .sidebar { display: none; }</span></span>
<span class="line"><span>  .main-content { width: 100%; }</span></span>
<span class="line"><span>}</span></span>
<span class="line"><span>\`\`\`</span></span></code></pre></div><h4 id="scenario-2-overriding-third-party-library-styles" tabindex="-1">Scenario 2: Overriding Third-party Library Styles <a class="header-anchor" href="#scenario-2-overriding-third-party-library-styles" aria-label="Permalink to &quot;Scenario 2: Overriding Third-party Library Styles&quot;">​</a></h4><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>cdn.example.com/bootstrap.min.css cssAppend://{test2.css}</span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`\`\` test2.css</span></span>
<span class="line"><span>/* Override Bootstrap default styles */</span></span>
<span class="line"><span>.btn-primary { background-color: #007bff !important; }</span></span>
<span class="line"><span>.container { max-width: 1400px; }</span></span>
<span class="line"><span>\`\`\`</span></span></code></pre></div><h4 id="scenario-3-adding-print-styles" tabindex="-1">Scenario 3: Adding Print Styles <a class="header-anchor" href="#scenario-3-adding-print-styles" aria-label="Permalink to &quot;Scenario 3: Adding Print Styles&quot;">​</a></h4><div class="language-txt vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>www.example.com/print.css cssAppend://{test3.css}</span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`\`\` test3.css</span></span>
<span class="line"><span>@media print {</span></span>
<span class="line"><span>  .no-print { display: none; }</span></span>
<span class="line"><span>  a::after { content: &quot; (&quot; attr(href) &quot;)&quot;; }</span></span>
<span class="line"><span>}</span></span>
<span class="line"><span>\`\`\`</span></span></code></pre></div><h2 id="related-protocols" tabindex="-1">Related Protocols <a class="header-anchor" href="#related-protocols" aria-label="Permalink to &quot;Related Protocols&quot;">​</a></h2><ol><li><p><strong>Inject Content Before the Response Body</strong>: <a href="./reqAppend.html">reqAppend</a></p><ul><li>Inserts content before the body of all types of responses.</li></ul></li><li><p><strong>Inject Content Before CSS Response Body</strong>: <a href="./cssPrepend.html">cssPrepend</a></p><ul><li>Inserts content before the CSS response body (opposite of <code>cssAppend</code>).</li></ul></li><li><p><strong>Replace CSS Response Content</strong>: <a href="./cssBody.html">cssBody</a></p><ul><li>Completely replaces the CSS response content (rather than appending).</li></ul></li><li><p><strong>Other Content Type Operations</strong>:</p><ul><li><a href="./jsAppend.html">jsAppend</a>: Appends content after JavaScript response bodies.</li><li><a href="./htmlAppend.html">htmlAppend</a>: Appends content after HTML response bodies.</li></ul></li></ol>`,43)]))}const g=s(o,[["render",i]]);export{u as __pageData,g as default};
